-- -----------------------------------------------------
-- procedure AdminAddorEdit
-- -----------------------------------------------------

DELIMITER $$
USE `footDb`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `AdminAddorEdit`(
IN _IdAdmin INT,
IN _Name varchar(45),
IN _CodeAdmin varchar(45),
IN _Salary int
)
BEGIN
	SELECT _IdAdmin AS 'idAdmin';
    
	IF _IdAdmin = 0 THEN
		INSERT INTO admin(Name,CodeAdmin,Salary)
        VALUES (_Name,_CodeAdmin,_Salary);
		COMMIT;
        SET _IdAdmin = LAST_INSERT_ID();
	ELSE 
		UPDATE admin
        SET
        Name = _Name,
        CodeAdmin = _CodeAdmin,
        Salary = _Salary
        WHERE IdAdmin = _IdAdmin;
	END IF;
END$$

DELIMITER ;

